mapper.select:
select  

from Inventory WHERE 1=1
 
     and  LIPBIN = #{lipbin,jdbcType=VARCHAR}
  
  
     and  LIGLPT = #{liglpt,jdbcType=VARCHAR}
  
  
     and  LIPQOH = #{lipqoh,jdbcType=NUMERIC}
  
  
     and  LIPBCK = #{lipbck,jdbcType=NUMERIC}
  
  
     and  LIPREQ = #{lipreq,jdbcType=NUMERIC}
  
  
     and  LIQWBO = #{liqwbo,jdbcType=NUMERIC}
  
  
     and  LIHCOM = #{lihcom,jdbcType=NUMERIC}
  
  
     and  LIPCOM = #{lipcom,jdbcType=NUMERIC}
  
  
     and  LIFCOM = #{lifcom,jdbcType=NUMERIC}
  
  
     and  LIFUN1 = #{lifun1,jdbcType=NUMERIC}
  
  
     and  LIQOWO = #{liqowo,jdbcType=NUMERIC}
  
  
     and  LIQTTR = #{liqttr,jdbcType=NUMERIC}
  
  
     and  LILOTS = #{lilots,jdbcType=VARCHAR}
  
  
     and  LILRCJ = #{lilrcj,jdbcType=DATE}
  
  
     and  LIOT1P = #{liot1p,jdbcType=NUMERIC}
  
  
     and  LIOT2P = #{liot2p,jdbcType=NUMERIC}
  
  
     and  LIOT1A = #{liot1a,jdbcType=NUMERIC}
  
  
     and  LIQTIN = #{liqtin,jdbcType=NUMERIC}
  
  
     and  LIQTO1 = #{liqto1,jdbcType=NUMERIC}
  
  
     and  LIQTO2 = #{liqto2,jdbcType=NUMERIC}
  
  
     and  LIQONL = #{liqonl,jdbcType=NUMERIC}
  
  
     and  LIQTRI = #{liqtri,jdbcType=NUMERIC}
  
  
     and  LIQTRO = #{liqtro,jdbcType=NUMERIC}
  
  
     and  LINCDJ = #{lincdj,jdbcType=TIMESTAMP}
  
  
     and  LIQTY1 = #{liqty1,jdbcType=NUMERIC}
  
  
     and  LIQTY2 = #{liqty2,jdbcType=NUMERIC}
  
  
     and  LICSID = #{licsid,jdbcType=NUMERIC}
  
  
     and  LIURCD = #{liurcd,jdbcType=VARCHAR}
  
  
     and  LIURDT = #{liurdt,jdbcType=DATE}
  
  
     and  LIURAT = #{liurat,jdbcType=NUMERIC}
  
  
     and  LIURAB = #{liurab,jdbcType=NUMERIC}
  
  
     and  LIURRF = #{liurrf,jdbcType=VARCHAR}
  
  
     and  LIUSER = #{liuser,jdbcType=VARCHAR}
  
  
     and  LIUPMJ = #{liupmj,jdbcType=TIMESTAMP}
  
  
     and  JDE_UpdateDate = #{jdeUpdatedate,jdbcType=TIMESTAMP}
  
  
     and  KT_ProcessDate = #{ktProcessdate,jdbcType=TIMESTAMP}
  
  
     and  KT_FailDate = #{ktFaildate,jdbcType=TIMESTAMP}
  
  
     and  KT_RetryTimes = #{ktRetrytimes,jdbcType=INTEGER}
  
  
     and  KT_ErrorLog = #{ktErrorlog,jdbcType=VARCHAR}
  
  
     and  fms_process_date = #{fmsProcessDate,jdbcType=TIMESTAMP}

select 

from Inventory
where LIITM = #{liitm,jdbcType=VARCHAR}
  and LIMCU = #{limcu,jdbcType=VARCHAR}
  and LILOCN = #{lilocn,jdbcType=VARCHAR}
  and LILOTN = #{lilotn,jdbcType=VARCHAR}

mapper.delete:
delete from Inventory
where LIITM = #{liitm,jdbcType=VARCHAR}
  and LIMCU = #{limcu,jdbcType=VARCHAR}
  and LILOCN = #{lilocn,jdbcType=VARCHAR}
  and LILOTN = #{lilotn,jdbcType=VARCHAR}
